package gh;

public class te {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int k = 0; k<20; k++) {	
			int count = 0;
			float floatcount = 0.0f;
			//float total = 0f;
		
			for (int i = 0; i<10 ; i++);{
				count++;
				floatcount += 0.1f;
		}
		System.out.println("Count: " + count);
		System.out.println("Float count: " + floatcount);
		if (floatcount == 1) {
			System.out.println("it's actually 10!");
		}

		
	}

}
}
